//NAVIGATION OF WEBSITE

//Variables
let footerArrow = document.querySelector("#footerArrow");
let contactButton = document.querySelector("#contactButton");
let contactContainer = document.querySelector("#contactContainer");
let home = document.querySelector("#home");
let about = document.querySelector("#about");
let contact = document.querySelector("#contact");
let infoContainer = document.querySelector("infoContainer");
let aboutContainer = document.querySelector("#aboutContainer");

//Navbar navigation
home.addEventListener("click", navigateHome, false);

function navigateHome(e) {
    infoContainer.scrollIntoView({
        behavior: "smooth"
    });
}

about.addEventListener("click", navigateAbout, false);

function navigateAbout(e) {
    aboutContainer.scrollIntoView({
        behavior: "smooth"
    });
}

contact.addEventListener("click", navigateContact, false);

function navigateContact(e) {
    contactContainer.scrollIntoView({
        behavior: "smooth"
    });
}


//Footer to Top View
footerArrow.addEventListener("click", scrollToTop, false);

function scrollToTop(e) {
    document.body.scrollIntoView({
        behavior: "smooth"
    });
}

//Contact Details Button

contactButton.addEventListener("click", navigateContact, false);

function navigateContact(e) {
    contactContainer.scrollIntoView({
        behavior: "smooth"
    });
}



/*ABOUT PAGE JS */
//Variables
let aboutBtn = document.getElementById("aboutBtn");
let workExp = document.getElementById("workExp");
let edu = document.getElementById("edu");
let skills = document.getElementById("skills");
let awards = document.getElementById("awards");
let lang = document.getElementById("lang");
let navInfo = document.getElementById("navInfo");

function selectAboutMeElement(currentActiveEle) {
    let aboutMeList = [workExp, edu, skills, awards, lang];
    aboutMeList.forEach(el => el.classList.remove('border-bottom-active'))
    currentActiveEle.classList.remove('border-bottom-white');
    currentActiveEle.classList.add('border-bottom-active');
    console.log(currentActiveEle.classList)
}

//Know Me Button
aboutBtn.addEventListener("click", scrollToTop, false);

function scrollToTop(e) {
    document.body.scrollIntoView({
        behavior: "smooth"
    });
}
